import React, { Component } from 'react';
import Script from 'react-load-script'
import Gamepad from 'react-gamepad'
import update from 'immutability-helper';

class List extends Component {
  // Initialize the state
  constructor(props){
    super(props);
    //this.onChange = this.onChange.bind(this);

    this.state = {
        out_data:[0, 0, 0, 0, 0, 0, 0, 0],
        a_button: 0,
        rt:0,
      list: []
    }

  }

    connectHandler(gamepadIndex) {
    console.log(`Gamepad ${gamepadIndex} connected !`)
    }

    disconnectHandler(gamepadIndex) {
    console.log(`Gamepad ${gamepadIndex} disconnected !`)
    }


    axisChangeHandler(axisName, value, previousValue) {
        console.log(axisName, value)
        if (axisName==="RightStickX")
            this.setState({
                out_data: update(this.state.out_data, {0: value})
            })
        if (axisName==="RightStickY")
            this.setState({
                out_data: update(this.state.out_data, {1: value})
            })
        if (axisName==="LeftStickY")
            this.setState({
                out_data: update(this.state.out_data, {2: value})
            })

    }
    onRT(){
        console.log("RT")
        /*this.setState({
            out_data: update(this.state.out_data, {3: 1})
        })*/
        this.setState({
            rt: 1
          });

    }  
    onButtonDown(buttonName) {
        console.log(buttonName)
        if (buttonName==="DPadUp")
            this.setState({
                out_data: update(this.state.out_data, {4: 1})
            })
        if (buttonName==="DPadRight")
            this.setState({
                out_data: update(this.state.out_data, {4: 2})
            })
        if (buttonName==="DPadDown")
            this.setState({
                out_data: update(this.state.out_data, {4: 3})
            })
        if (buttonName==="DPadLeft")
            this.setState({
                out_data: update(this.state.out_data, {4: 4})
            })
    }

    a_handler(){



    }
    onA(){
        console.log("A")
        /*this.setState({
            out_data: update(this.state.out_data, {5: 1})
        })*/
      //sendAButton("A")
    }


    onLB(){
        console.log("LB")
        this.setState({
            out_data: update(this.state.out_data, {6: 1})
        })
     }
     onRB(){
        console.log("RB")
        this.setState({
            out_data: update(this.state.out_data, {7: 1})
        })

     }
  // Fetch the list on first mount
  componentDidMount() {
    this.sendData();
    //this.reportOnGamepad();

  }

sendData = () =>{
    fetch('/api/xsend', {  
        method: 'post',
        headers: {'Content-Type': 'application/json'},
        body: JSON.stringify({
            data : "50,50",
            }),
        }) 
};

  // Retrieves the list of items from the Express app



  render() {
    const { list } = this.state;

    return (
      <div className="App">
      <Gamepad
        onConnect={this.connectHandler}
        onDisconnect={this.disconnectHandler}
        onA={this.onA}
        onRT={this.onRT}


      >
     <p></p>
    </Gamepad>

        <h1>List of Items</h1>
        {/* Check to see if any items are found*/}
        {list.length ? (
          <div>
            {/* Render the list of items */}
            {list.map((item) => {
              return(
                <div>
                  {item}
                </div>
              );
            })}
          </div>
        ) : (
          <div>
            <h2>No List Items Found</h2>

            {this.state.out_data}
          </div>
        )
      }
      </div>
    );
  }

}

export default List;
