import React, { Component } from 'react';
import Script from 'react-load-script'
import Gamepad from 'react-gamepad'

class List extends Component {
  // Initialize the state
  constructor(props){
    super(props);
    this.state = {
        out_data:"",

      list: []
    }
  }

  // Fetch the list on first mount
  componentDidMount() {
    this.sendData();
    this.reportOnGamepad();

  }

sendData = () =>{
    fetch('/api/xsend', {  
        method: 'post',
        headers: {'Content-Type': 'application/json'},
        body: JSON.stringify({
            data : "50,50",
            }),
        }) 
};

  // Retrieves the list of items from the Express app
  getList = () => {
    fetch('/api/getList')
    .then(res => res.json())
    .then(list => this.setState({ list }))
  }

  reportOnGamepad = () => {
    var rt;
	var d_pad;
	var a_button;
	var l_bump;
	var r_bump;
    var gp = navigator.getGamepads()[0];

    if(gp.buttons[8].pressed) 
        rt= 1;
    else
        rt=0;
    if(gp.buttons[1].pressed) 
        a_button= 1;
    else
        a_button=0;
    if(gp.buttons[5].pressed) 
        l_bump= 1;
    else
        l_bump=0;
    if(gp.buttons[6].pressed) 
        r_bump= 1;
    else
        r_bump=0;

    if(gp.buttons[13].pressed) 
        d_pad= 1;
    else if(gp.buttons[16].pressed) 
        d_pad= 2;
    else if(gp.buttons[14].pressed) 
        d_pad= 3;
    else if(gp.buttons[15].pressed) 
        d_pad= 4;
    else
        d_pad=0;




    var out =  gp.axes[2]*127+","+gp.axes[3]*127+","+gp.axes[1]*127+","+rt+","+d_pad+","+a_button+","+l_bump+","+r_bump;
    console.log(out);
    this.setState({out_data: out})

 }
  render() {
    const { list } = this.state;

    return (
      <div className="App">


        <h1>List of Items</h1>
        {/* Check to see if any items are found*/}
        {list.length ? (
          <div>
            {/* Render the list of items */}
            {list.map((item) => {
              return(
                <div>
                  {item}
                </div>
              );
            })}
          </div>
        ) : (
          <div>
            <h2>No List Items Found</h2>
            {this.state.out_data}
          </div>
        )
      }
      </div>
    );
  }

}

export default List;
