import React, { Component } from 'react';
import Script from 'react-load-script'
import Gamepad from 'react-gamepad'
import update from 'immutability-helper';

class List extends Component {
  // Initialize the state
  constructor(props){
    super(props);
    this.onRT = this.onRT.bind(this);
    this.onA = this.onA.bind(this);
    this.axisChangeHandler = this.axisChangeHandler.bind(this);
    this.onButtonDown = this.onButtonDown.bind(this);
    this.onLB = this.onLB.bind(this);
    this.onRB = this.onRB.bind(this);

    this.state = {
        out_data:"",
        a_button: 1,
        rsx:0,
        rsy:0,
        lsy:0,
        rt:23,
        dpad: 0,
        lb: 0,
        rb: 0,
        seconds: 0,
      list: []
    }

  }
    tick() {
        this.setState(prevState => ({
        seconds: prevState.seconds + 0.5
        }));
        this.sendData();

    }




    connectHandler(gamepadIndex) {
    console.log(`Gamepad ${gamepadIndex} connected !`)
    }

    disconnectHandler(gamepadIndex) {
    console.log(`Gamepad ${gamepadIndex} disconnected !`)
    }


    axisChangeHandler(axisName, value, previousValue) {
        console.log(axisName, value)
        if (axisName==="RightStickX")
            this.setState({
                rsx: value*127
            });
        if (axisName==="RightStickY")
            this.setState({
                rsy: value*127
            });   
        if (axisName==="LeftStickY")
            this.setState({
                lsy: value*127
            });   

    }
    onRT(){
        console.log("RT")
        /*this.setState({
            out_data: update(this.state.out_data, {3: 1})
        })*/
        this.setState({
            rt: 1
          });

    }  
    onButtonDown(buttonName) {
        console.log(buttonName)
        if (buttonName==="DPadUp")
        this.setState({
            dpad: 1
          });
        if (buttonName==="DPadRight")
        this.setState({
            dpad: 2
          });
        if (buttonName==="DPadDown")
            this.setState({
                dpad: 3
            });
        if (buttonName==="DPadLeft")
        this.setState({
            dpad: 4
          });
    }


    onA(){
        console.log("A")
        this.setState({
            a_button: 1
          });
    }


    onLB(){
        console.log("LB")
        this.setState({
            lb: 1
          });
     }
     onRB(){
        console.log("RB")
        this.setState({
            rb: 1
          });

     }
  // Fetch the list on first mount
componentDidMount() {
    this.interval = setInterval(() => this.tick(), 500);


    //this.reportOnGamepad();

  }

  componentWillUnmount() {
    clearInterval(this.interval);
  }
sendData = () =>{
   var out = this.state.rsx+","+this.state.rsx+","+this.state.lsy+","+this.state.rt+","+this.state.dpad+","+this.state.a_button+","+this.state.lb+","+this.state.rb;
    fetch('/api/xsend', {  
        method: 'post',
        headers: {'Content-Type': 'application/json'},
        body: JSON.stringify({
            data : out,
            }),
        }) 
};

  // Retrieves the list of items from the Express app



  render() {
    const { list } = this.state;

    return (
      <div className="App">
      <Gamepad
        onConnect={this.connectHandler}
        onDisconnect={this.disconnectHandler}
        onButtonDown={this.onButtonDown}
        onAxisChange={this.axisChangeHandler}
        onLB={this.onLB}
        onRB={this.onRb}
        onA={this.onA}
        onRT={this.onRT}


      >
     <p></p>
    </Gamepad>
    <div>
                Seconds: {this.state.seconds}
              </div>
        <h1>List of Items</h1>
        {/* Check to see if any items are found*/}
        {list.length ? (
          <div>
            {/* Render the list of items */}
            {list.map((item) => {
              return(
                <div>
                  {item}
                </div>
              );
            })}
          </div>
        ) : (
          <div>
            <h2>No List Items Found</h2>

            {this.state.rsx},{this.state.rsx},{this.state.lsy},{this.state.rt},{this.state.dpad},{this.state.a_button},{this.state.lb},{this.state.rb}
          </div>
        )
      }
      </div>
    );
  }

}

export default List;
